import React, { Component } from 'react';

class PostContainer extends Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: []
        }
    }

    componentDidMount(){
        this.readAllPosts();
    }

    readAllPosts = async () => {
        const allPosts = await indexPosts();
        this.setState({
            posts: allPosts
        })
    }

    render() {
        return (
            <div>
                <Route exact path="/posts" render={() => {
                return <PostList posts={this.state.posts} />
            }} /> 
            </div>
        )
    }
}

export default PostContainer;
