import React, { Component } from 'react';
import './App.css';
import { Route } from 'react-router-dom';

import SignUp from './Components/SignUp';
import Login from './Components/Login';

import { signUpUser } from './Service/api_helper';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentUser: null
    }
  }

  handleSignUp = async (e, user) => {
    e.preventDefault();    
    const loadedUser = await signUpUser(user)
    this.setState({
      currentUser: loadedUser
    })
    
  }


  handleLogin = async (e, user) => {
    e.preventDefault();
    const loadedUser = await loginUser(user);

    this.setState({
      currentUser: loadedUser
    })
  }

  render() {
  return (
    <div className="App">
      <header className="App-header">
        <h2>Wayfarer</h2>
      <SignUp handleSubmit={this.handleSignUp} />
      <Login handleSubmit={this.handleLogin}/>
      </header>
    </div>
  );
}
}

export default App;
