import React, { Component } from 'react'
import { getAllCities } from '../Service/api_helper'

class Show extends Component {
    constructor(props) {
        super(props);

        this.state = {
            cities: []
        }
    }

    getCities = async () => {
        const allCities = await getAllCities();
        this.setState({
            cities: allCities
        })
    }

    componentDidMount() {
        this.getCities();
    }


    render() {
        return (
            <div>
                <h2>Show Post Page</h2>
                {/* <nav>

                </nav> */}
            </div>
        )
    }
}

export default Show;
