import React, {Component} from 'react'
import InlineEdit from 'react-edit-inline';

class Profile extends Component {
    constructor(props) {
    super(props);

    this.dataChanged = this.dataChanged.bind(this);

    this.state = {
        currentProfile: props.userProfile,
        name: ''
    }
    }

    dataChange(e) {
        this.setState({name: e.target.value})
    }

    customValidateText(text) {
        return (text.length > 0 && text.length < 64);
      }

    render() {    
    return (
        <div>
            <form> 
            <h1>{this.state.currentProfile.name}</h1>
            <InlineEdit
              validate={this.customValidateText}
              activeClassName="editing"
              text={this.state.message}
              paramName="message"
              change={this.dataChanged} />
            <br></br>           
            <h2>{this.state.currentProfile.username}</h2>
            <br></br>
            <h2>{this.state.currentProfile.img}</h2>
            <br></br>
            <h2>{this.state.currentProfile.createdAt}</h2>
            <br></br>
            <h2>{this.state.currentProfile.City}</h2> 
              
            </form>
        </div>
    )  
} 

}


export default Profile;
