import React, {Component} from 'react';


class Profile extends Component {
    constructor(props) {
    super(props);

    this.state = {
        name: props.profile.name,
        username:props.profile.username,
        img:props.profile.img,
        City: props.profile.City,
        createdAt: props.profile.createdAt     
    }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {    
    return (
        <div>
<form onSubmit={(e) => this.props.updateUser(e, this.state)}>
                <h1>Edit Account</h1>
                <input type="text" name="name" placeholder={this.state.name} onChange={this.handleChange} />
                <input type="text" name="username" placeholder={this.state.username} onChange={this.handleChange}/>
                <input type="text" name="img" placeholder={this.state.img} onChange={this.handleChange}/>
                <input type="text" name="City" placeholder={this.state.City} onChange={this.handleChange}/>
                <input type="submit" value="Submit Post" />
           </form>
           <p>{this.state.createdAt}</p>
        </div>
    )  
} 

}


export default Profile;
